fun main() {
    //tests1()
    tests2()

    part1(input)
    part2(input)
}

class Floorcounter {

    fun move(directions: String): Int {
        var current = 0
        for(char in directions) {
            if (char == '(') {
                current++
            } else if (char == ')') {
                current--
            }
        }

        return current
    }

    fun moveToBasement(directions: String): Int {
        var current = 0

        directions.forEachIndexed { index, char ->

            if (char == '(') {
                current++
            } else if (char == ')') {
                current--
            }

            // Check if we are in the basement
            if(current == -1) {
                return index + 1
            }
        }

        throw IllegalStateException("Count not find basement.")
    }
}

private val tests1 = mapOf(
    "(())" to 0,
    "()()" to 0,
    "(((" to 3,
    "(()(()(" to 3,
    "))(((((" to 3,
    "())" to -1,
    "))(" to -1,
    ")))" to -3,
    ")())())" to -3,
)

private val tests2 = mapOf(
    ")" to 1,
    "()())" to 5,
)


private fun tests1() {
    for((test, expected) in tests1) {
        println("testing $test (should be $expected)")
        val counter = Floorcounter()
        val result = counter.move(test)
        assert(result == expected) { "wrong result" }
    }
}

private fun tests2() {
    for((test, expected) in tests2) {
        println("testing $test (should be $expected)")
        val counter = Floorcounter()
        val result = counter.moveToBasement(test)
        assert(result == expected) { "wrong result" }
    }
}

private fun part1(directions: String) {
    val counter = Floorcounter()

    val result = measureTimedValue {
        counter.move(directions)
    }

    println("part 1: ${result.value} (in ${result.duration})")
}

private fun part2(directions: String) {
    val counter = Floorcounter()

    val result = measureTimedValue {
        counter.moveToBasement(directions)
    }

    println("part 2: ${result.value} (in ${result.duration})")
}
