package year2017

import kotlin.text.iterator

// start-of-packet marker

// four characters that are all different

/*
The device will send your subroutine a datastream buffer (your puzzle input); your subroutine needs to identify the first position where the four most recently received characters were all different.
Specifically, it needs to report the number of characters from the beginning of the buffer to the end of the first such four-character marker.
 */

val input1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb" // 7
val input2 = "bvwbjplbgvbhsrlpgdmjqwftvncz" // first marker after character 5
val input3 = "nppdvjthqldpwncqszvftbrmjlhg" //  first marker after character 6
val input4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" // first marker after character 10
val input5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" // first marker after character 11

//
val messageInput1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb" // first marker after character 19
val messageInput2 = "bvwbjplbgvbhsrlpgdmjqwftvncz" // first marker after character 23
val messageInput3 = "nppdvjthqldpwncqszvftbrmjlhg" // first marker after character 23
val messageInput4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" // first marker after character 29
val messageInput5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" // first marker after character 26

//
val realInput = ""

fun getStartMarker(input: String, length: Int): Int {
    println("input: $input ---------------------------")
    var count = 0
    var list = mutableListOf<Char>()

    for(char in input) {
        println("char: $char")
        if(list.contains(char)) {
            println("(cleaning) already had $char (list: $list)")

            val index = list.indexOfFirst { it == char }
            println("index of [$char] was $index")

            list = list.drop(index+1).toMutableList()
            println("new list: $list")
        }

        list.add(char)

        if(list.size == length){
            println("(done) found $length characters: $list")

            break
        }

        count++
    }

    println("(done) list: $list")
    return count + 1
}

fun main(){
    val first = getStartMarker(input1, 4)
    println("first: $first (should be 7)")

    val second = getStartMarker(input2, 4)
    println("second: $second (should be 5)")

    val third = getStartMarker(input3, 4)
    println("third: $third (should be 6)")

    val fourth = getStartMarker(input4, 4)
    println("fourth: $fourth (should be 10)")

    val fifth = getStartMarker(input5, 4)
    println("fifth: $fifth (should be 11)")

    val real = getStartMarker(realInput, 4)
    println("real: $real")

}
